{
  "name": "Complex Configuration Rule",
  "entity_type": "Commission Request",
  "description": "A complex rule with deeply nested conditions",
  "conditions": {
    "all": [
      {
        "any": [
          {
            "path": "$.configs[*].type",
            "operator": "equal",
            "value": "firewall"
          },
          {
            "all": [
              {
                "path": "$.configs[*].type",
                "operator": "equal",
                "value": "router"
              },
              {
                "path": "$.configs[*].security_level",
                "operator": "greater_than_equal",
                "value": 3
              }
            ]
          }
        ]
      },
      {
        "none": [
          {
            "path": "$.configs[*].status",
            "operator": "equal",
            "value": "deprecated"
          },
          {
            "not": {
              "path": "$.configs[*].compliance_checked",
              "operator": "equal",
              "value": true
            }
          }
        ]
      }
    ]
  }
}

{
  "entity_type": "network",
  "category": "security",
  "rules": [
    {
      "name": "Firewall Rule",
      "entity_type": "Commission Request",
      "description": "Check if firewall is enabled",
      "conditions": {
        "all": [
          {
            "path": "$.networks[*].firewall_enabled",
            "operator": "equal",
            "value": true
          }
        ]
      }
    },
    {
      "name": "Encryption Rule",
      "entity_type": "Commission Request",
      "description": "Check if encryption is enabled",
      "conditions": {
        "all": [
          {
            "path": "$.networks[*].encryption",
            "operator": "equal",
            "value": "AES-256"
          }
        ]
      }
    },
    {
      "name": "VPN Rule",
      "entity_type": "Commission Request",
      "description": "Check if VPN is set up",
      "conditions": {
        "all": [
          {
            "path": "$.networks[*].vpn_configured",
            "operator": "equal",
            "value": true
          }
        ]
      }
    }
  ]
}

